float Hash(float2 p)
{
    float3 p2 = float3(p.xy, 1.0);
    return fract(sin(dot(p2, float3(37.1, 61.7, 12.4))) * 3758.5453123);
}

float noise(float2 p)
{
    float2 i = floor(p);
    float2 f = fract(p);
    f *= f * (3.0 - 2.0 * f);

    return mix(mix(Hash(i + float2(0.0, 0.0)), Hash(i + float2(1.0, 0.0)), f.x),
               mix(Hash(i + float2(0.0, 1.0)), Hash(i + float2(1.0, 1.0)), f.x),
               f.y);
}

float fbm(float2 p)
{
    float v = 0.0;
    v += noise(p * 1.0) * 0.5;
    v += noise(p * 2.0) * 0.25;
    v += noise(p * 4.0) * 0.125;
    return v;
}

 float4 burnTransition(float2 uv, float3 src, float3 tgt, float ctime)
{



    float3 col = src;

    uv.x -= 1.5;



    // burn
    float d = uv.x + uv.y * 0.5 + 0.5 * fbm(uv * 15.1) + ctime * 1.3;
    if (d > 0.35) col = clamp(col - (d - 0.35) * 10.0, 0.0, 1.0);
    if (d > 0.47) {
        if (d < 0.5) col += (d - 0.4) * 33.0 * 0.5 * (0.0 + noise(100.0 * uv + float2(-ctime * 2.0, 0.0))) * float3(1.5, 0.5, 0.0);
        else col += tgt;
    }

    return float4(col, 1.0);
}

