float3 swirl(texture2d<float> tex, float2 uv, float time)
{
    float2 size = float2(tex.get_width(), tex.get_height());

    float2 center = float2(size.x * 0.5, size.y * 0.5);

    float radius = time * max(size.x, size.y);
    float angle = time * 2.0;

    float2 texSize = float2(size.x, size.y);
    float2 tc = uv * texSize;
    tc -= center;
    float dist = length(tc);
    if (dist < radius)
    {
        float percent = (radius - dist) / radius;
        float theta = percent * percent * angle * 8.0;
        float s = sin(theta);
        float c = cos(theta);
        tc = float2(dot(tc, float2(c, -s)), dot(tc, float2(s, c)));
    }
    tc += center;

    float3 color = tex.sample(sam, tc/texSize).rgb;// swirlTransition(tc / texSize, tex, tex, uv, uv, sin(time));
    return color;
}

float3 swirlTransition(float2 texCoord,texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time) {


    float3 color1 = tex1.sample(sam, uvFirst).rgb;
    float3 color2 = tex2.sample(sam, uvSec).rgb;


    float3 color = mix(
        swirl(tex1, uvFirst, sin(time * PI)),
        swirl(tex2, uvSec, sin(time * PI)),
                time
    );


    return (color);
}



float Linear_ease( float begin,  float change,  float duration,  float time) {
    return change * time / duration + begin;
}

float Exponential_easeInOut( float begin,  float change,  float duration,  float time) {
    if (time == 0.0)
        return begin;
    else if (time == duration)
        return begin + change;
    time = time / (duration / 2.0);
    if (time < 1.0)
        return change / 2.0 * pow(2.0, 10.0 * (time - 1.0)) + begin;
    return change / 2.0 * (-pow(2.0, -10.0 * (time - 1.0)) + 2.0) + begin;
}

float Sinusoidal_easeInOut( float begin,  float change,  float duration,  float time) {
    return -change / 2.0 * (cos(PI * time / duration) - 1.0) + begin;
}

float random( float3 scale,  float seed, float2 uv) {
    return fract(sin(dot(uv.xyx + seed, scale)) * 43758.5453 + seed);
}

float3 crossFade(texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,  float dissolve) {
    return mix(tex1.sample(sam, uvFirst).rgb, tex2.sample(sam, uvSec).rgb, dissolve);
}

float3 zoomTransition(float2 texCoord,texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time) {

    float progress = time;
    // Linear interpolate center across center half of the image
    float2 center = float2(Linear_ease(0.5, 0.0, 1.0, progress),0.5);
    float dissolve = Exponential_easeInOut(0.0, 1.0, 1.0, progress);

    // Mirrored sinusoidal loop. 0->strength then strength->0
    float strength = Sinusoidal_easeInOut(0.0, 0.3, 0.5, progress);

    float3 color = float3(0.0);
    float total = 0.0;
    float2 toCenter = center - texCoord;

    /* randomize the lookup values to hide the fixed number of samples */
    float offset = random(float3(12.9898, 78.233, 151.7182), 0.0, texCoord)*0.5;

    for (float t = 0.0; t <= 20.0; t++) {
        float percent = (t + offset) / 20.0;
        float weight = 1.0 * (percent - percent * percent);
        color += crossFade(tex1,
                           tex2,
                           uvFirst + toCenter * percent * strength,
                           uvSec + toCenter * percent * strength, dissolve) * weight;
        total += weight;
    }

    return (color / total);
}



float3 screenMelt(float2 texCoord,texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time)
{

    // initial "paint melt" speed
    const float START_SPEED  = 2.7;
    // texture melting off screen speed
    const float MELT_SPEED   = 1.;

    float2 uv = uvFirst;
    float2 p = uv;

    // first let some "paint" drip before moving entire texture contents
    float d = (START_SPEED * time);
    if(d > 1.) d = 1.;

    // initial paint melt shift
    p.y -= d * 1. * fract(sin(dot(float2(p.x, .0), float2(12.9898, 78.233)))* 43758.5453);

    // now move entire melted texture offscreen
    if(d == 1.)
        p.y -= MELT_SPEED * (time - d/START_SPEED);

    float3 color1 = tex1.sample(sam, p).rgb;
    float3 color2 = tex2.sample(sam, uvSec).rgb;
    float3 color = mix(color1, color2, time);

    return color;
}

float3 crosswarp(float2 texCoord,texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time)
{
  float2 p = uvFirst;
    float2 p2 = uvSec;
  float x = mod(time,2.); // change this to alter the speed

  x = smoothstep(.0,1.0,(x*2.0+p.x-1.0));
    float pc1x = (p.x -.5)*(1.-x)+.5;
    float pc1y = (p.y -.5)*(1.-x)+.5;
    float2 pc1 = float2(pc1x, pc1y);

    float pc2x = (p2.x - .5)*x+.5;
    float pc2y = (p2.y - .5)*x+.5;
    float2 pc2 = float2(pc2x, pc2y);

    float3 color1 = tex1.sample(sam, pc1).rgb;
    float3 color2 = tex2.sample(sam, pc2).rgb;
    float3 color = mix(color1, color2, x);

    return color;
}
float3 crossHatch(float2 texCoord,texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time)
{
    float threshold = 3.;
    float fadeEdge = 0.1;
    float2 center = float2(0.5);

    float iTime2 = fract(time/1.5);
    float2 uv = uvFirst;
    float dist = distance(center, uv) / threshold;
    float r = iTime2 - min(rand(float2(uv.y, 0.0)), rand(float2(0.0, uv.x)));

    float3 c1 = tex1.sample(sam, uvFirst).rgb;
    float3 c2 = tex2.sample(sam, uvSec).rgb;

    float3 c3 = mix(c1, c2, mix(0.0, mix(step(dist, r), 1.0, smoothstep(1.0-fadeEdge, 1.0, iTime2)), smoothstep(0.0, fadeEdge, iTime2)));
    return c3;
}

float3 circularTiles(float2 first_res,texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time)
{
    float aspectRatio = first_res.x / first_res.y;

    // Normalized pixel coordinates (from 0 to 1)
    float2 uv = uvFirst;

    // this keeps the transition always in circle shape instead of
    // aspect ratio dependent
    uv.x *= aspectRatio;

    // This line tiles the UVs into 10 parts
    uv = fract(uv * 10.0);

    // only required for circular tiling
    uv -= 0.5; // shifts the 0.0 point to the middle of each tile


    // Circular tile
    float timing = sin(time) * 1.42;
    float value = step(timing, length(uv));


    float3 color1 = tex1.sample(sam, uvFirst).rgb;
    float3 color2 = tex2.sample(sam, uvSec).rgb;
    float3 color = mix(color2, color1, min(value, 1.));

    return color;
}

float3 rectangularTiles(float2 first_res,texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time)
{
    float aspectRatio = first_res.x / first_res.y;

    // Normalized pixel coordinates (from 0 to 1)
    float2 uv = uvFirst;

    // this keeps the transition always in circle shape instead of
    // aspect ratio dependent
    uv.x *= aspectRatio;

    // This line tiles the UVs into 10 parts
    uv = fract(uv * 10.0);

    // only required for circular tiling
    uv -= 0.5; // shifts the 0.0 point to the middle of each tile

    // center tile wipe
    float timing = sin(time);
    float value = step(timing, abs(uv.x)) + step(timing, abs(uv.y));

    float3 color1 = tex1.sample(sam, uvFirst).rgb;
    float3 color2 = tex2.sample(sam, uvSec).rgb;
    float3 color = mix(color2, color1, min(value, 1.));

    return color;
}
