float3 blockyDissolve(float2 first_res,texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time)
{
    // increase this value to increase the size of block
    float resolution = 20.0;
    float2 lowresxy = float2(
                             ceil((uvFirst.x * first_res.x) / resolution),
                             ceil((uvFirst.y * first_res.y) / resolution)
                             );

