        return color2;

    }else{
        float3 color1 = tex1.sample(sam, uvFirst).rgb;
        return color1;
    }
}




float3 mod289(float3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

float4 mod289(float4 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

float4 permute(float4 x) {
  return mod289(((x*34.0)+1.0)*x);
}

float4 taylorInvSqrt(float4 r){
  return 1.79284291400159 - 0.85373472095314 * r;
}

float snoise(float3 v) {

  const float2  C = float2(1.0/6.0, 1.0/3.0) ;
  const float4  D = float4(0.0, 0.5, 1.0, 2.0);

  // First corner
  float3 i  = floor(v + dot(v, C.yyy) );
  float3 x0 =   v - i + dot(i, C.xxx) ;

  // Other corners
  float3 g = step(x0.yzx, x0.xyz);
  float3 l = 1.0 - g;
  float3 i1 = min( g.xyz, l.zxy );
  float3 i2 = max( g.xyz, l.zxy );

  //   x0 = x0 - 0.0 + 0.0 * C.xxx;
  //   x1 = x0 - i1  + 1.0 * C.xxx;
  //   x2 = x0 - i2  + 2.0 * C.xxx;
  //   x3 = x0 - 1.0 + 3.0 * C.xxx;
  float3 x1 = x0 - i1 + C.xxx;
  float3 x2 = x0 - i2 + C.yyy; // 2.0*C.x = 1/3 = C.y
  float3 x3 = x0 - D.yyy;      // -1.0+3.0*C.x = -0.5 = -D.y

  // Permutations
  i = mod289(i);
  float4 p = permute( permute( permute(
      i.z + float4(0.0, i1.z, i2.z, 1.0 ))
    + i.y + float4(0.0, i1.y, i2.y, 1.0 ))
    + i.x + float4(0.0, i1.x, i2.x, 1.0 ));

  // Gradients: 7x7 points over a square, mapped onto an octahedron.
  // The ring size 17*17 = 289 is close to a multiple of 49 (49*6 = 294)
  float n_ = 0.142857142857; // 1.0/7.0
  float3  ns = n_ * D.wyz - D.xzx;

  float4 j = p - 49.0 * floor(p * ns.z * ns.z);  //  mod(p,7*7)

  float4 x_ = floor(j * ns.z);
  float4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  float4 x = x_ *ns.x + ns.yyyy;
  float4 y = y_ *ns.x + ns.yyyy;
  float4 h = 1.0 - abs(x) - abs(y);

  float4 b0 = float4( x.xy, y.xy );
  float4 b1 = float4( x.zw, y.zw );

  //float4 s0 = float4(lessThan(b0,0.0))*2.0 - 1.0;
  //float4 s1 = float4(lessThan(b1,0.0))*2.0 - 1.0;
  float4 s0 = floor(b0)*2.0 + 1.0;
  float4 s1 = floor(b1)*2.0 + 1.0;
  float4 sh = -step(h, float4(0.0));

  float4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  float4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  float3 p0 = float3(a0.xy,h.x);
  float3 p1 = float3(a0.zw,h.y);
  float3 p2 = float3(a1.xy,h.z);
  float3 p3 = float3(a1.zw,h.w);

  //Normalise gradients
  float4 norm = taylorInvSqrt(float4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

  // Mix final noise value
  float4 m = max(0.6 - float4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, float4( dot(p0,x0), dot(p1,x1), dot(p2,x2), dot(p3,x3) ) );

}

// via: https://petewerner.blogspot.jp/2015/02/intro-to-curl-noise.html
float3 curlNoise( float3 p ){

  const float e = 0.1;

  float  n1 = snoise(float3(p.x, p.y + e, p.z));
  float  n2 = snoise(float3(p.x, p.y - e, p.z));
  float  n3 = snoise(float3(p.x, p.y, p.z + e));
  float  n4 = snoise(float3(p.x, p.y, p.z - e));
  float  n5 = snoise(float3(p.x + e, p.y, p.z));
  float  n6 = snoise(float3(p.x - e, p.y, p.z));

  float x = n2 - n1 - n4 + n3;
  float y = n4 - n3 - n6 + n5;
  float z = n6 - n5 - n2 + n1;


  const float divisor = 1.0 / ( 2.0 * e );
  return normalize( float3( x , y , z ) * divisor );
}

float3 curlNoise(texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time) {
    float3 curl = curlNoise(float3(uvFirst,1.) *5. + time) / 1.;
    float3 color1 = tex1.sample(sam, float2(uvFirst.x,uvFirst.y + time * (curl.x) )).rgb;
    float3 color2 = tex2.sample(sam, float2(uvSec.x,uvSec.y + (1.-time) * (curl.x) )).rgb;
    float3 color = mix(color1, color2, time);
    return color;
}
float rule(float2 coord)
{
    float2 uv2 = coord - float2(0.5, 0.5);
    float angle = atan2(uv2.x, uv2.y);
    return angle / 2.0 / 3.1416 + 0.5;
}

float3 circularScan(texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time)
{
    float2 uv = uvFirst;
    float3 color1 = tex1.sample(sam, uvFirst).rgb;
    float3 color2 = tex2.sample(sam, uvSec).rgb;

    float myRule = rule(uv);
    float progress = time;
    float vague = 0.2;
    float phase = progress * (1.0 + vague);
    float p0 = smoothstep(myRule, myRule + vague, phase);

    float3 color = color1 * (1.0 - p0) + color2 * p0;
    return color;
}
float3 drain(texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time)
{
    float2 uv = uvFirst;
    float2 target = float2(0.5, 0.5);

    float strength = 1.0;

    //offset the uv (radial since we normalise the dir)
    float2 dir = normalize(uv - target);
    uv += dir * strength * (time);

    //if outside the texture region, return tex2
    if (any(uv < float2(0.0)) || any(uv > float2(1.0))) {
        float3 color2 = tex2.sample(sam, uvSec).rgb;
        return color2;
    } else {
        float3 color1 = tex1.sample(sam, uv).rgb;
        return color1;
    }
}

float3 stretchRight(texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time) {

    if (time < 0.5) {

        // we process stretch for tex1 here
        float2 uv = uvFirst;
        // time 0 is no stretch period and 1 is max stretch period
        // making time oscillate from 0 to 1
        float timeToUse = (time * 2.0);

        // non stretching x
        float nonStretchingX = 0.0;

        uv.x -= (uv.x - nonStretchingX) * timeToUse;

        float3 color1 = tex1.sample(sam, uv).rgb;
        return color1;
    } else {
        // we process stretch for tex2 here
        float2 uv = uvSec;

        // making time oscillate from 1 to 0
        float timeToUse = abs((time - 1) * 2);

        // non stretching x
        float nonStretchingX = 1.0;
        uv.x -= (uv.x - nonStretchingX) * timeToUse;
        float3 color2 = tex2.sample(sam, uv).rgb;
        return color2;
    }
}

float3 stretchLeft(texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time) {

