        float timeToUse = (time * 2.0);

        // non stretching x
        float nonStretchingX = 1.0;

        uv.x -= (uv.x - nonStretchingX) * timeToUse;

        float3 color1 = tex1.sample(sam, uv).rgb;
        return color1;
    } else {
        // we process stretch for tex2 here
