    }
}


float3 split(texture2d<float> tex1,texture2d<float> tex2, float2 uvFirst,float2 uvSec,float time) {

    float t = time;
    float2 uv = uvFirst;

    if (uv.y > 0.5) {
        t = 1.0 - t;
        if (uv.x < t) {
            uv.x += 1.0 - t;
            float3 color1 = tex1.sample(sam, uv).rgb;
            return color1;
        } else {
            uvSec.x -= t;
            float3 color2 = tex2.sample(sam, uvSec).rgb;
            return color2;
        }
    } else {

        if (uv.x < t) {
            uvSec.x += 1.0 - t;
            float3 color2 = tex2.sample(sam, uvSec).rgb;
            return color2;
        } else {
            uv.x -= t;
            float3 color1 = tex1.sample(sam, uv).rgb;
            return color1;
        }
    }
}


