float2 rotateUV(float2 uv, float rotation)
{
    return float2(
        cos(rotation) * uv.x + sin(rotation) * uv.y,
        cos(rotation) * uv.y - sin(rotation) * uv.x
    );
}
float2x2 rotate(float a) {
    float s = sin(a);
    float c = cos(a);
    return float2x2(c, -s, s, c);
}



typedef struct {
