float2 reduceAspectRatio(float2 uv, float2 source_res,float2 main_resolution, float a){


    uv -= 0.5;
    uv = rotateUV(uv, a);
    uv += 0.5;
    source_res *= rotate(a);

    if(a > 0){
        uv.y = 1.0 - uv.y;
    }

    float aspectMain = main_resolution.y/main_resolution.x;
    float aspectSource = source_res.y/source_res.x;

            if(aspectSource >= aspectMain){
                uv -= 0.5;
                uv.x *= aspectSource;
                uv.x /= aspectMain;
                uv += 0.5;
            }else{
                uv -= 0.5;
                uv.y /= aspectSource;
                uv.y *= aspectMain;
                uv += 0.5;
            }
//
    return uv;
}

